$(function(){$(".intro").height($(window).height()),$(window).resize(function(){$(".intro").height($(window).height())}),$(".toggle").click(function(){$(this).removeClass("fa-toggle-of").toggleClass("fa-toggle-on"),$("aside").toggleClass("showSideBar")}),$(".card-image, .card-title").click(function(){let o=`.card-info-${$(this).attr("data-value")}`;$(`.projects .card ${o}`).slideDown("slow",function(){console.log($(this)),$(`.projects .card ${o} .fa-times`).click(function(){$(`.projects .card ${o}`).slideUp("slow")})})}),$(".about").click(function(){$("html, body").animate({scrollTop:$(".about-us").offset().top},1e3)}),$(".experience").click(function(){$("html, body").animate({scrollTop:$(".experiences").offset().top},1e3)}),$(".project").click(function(){$("html, body").animate({scrollTop:$(".projects").offset().top},1e3)})});